import React from 'react'

const style = {
    marginRight: 10
};
class List extends React.Component {
    constructor() {
        super()
        this.state = {
            description: [
                {
                    des: 'Description',
                    color: 'Green',
                    name: 'car1'
                },
                {
                    des: 'Description',
                    color: 'Green',
                    name: 'car2'
                },
                {
                    des: 'Description',
                    color: 'Green',
                    name: 'car3'
                },
                {
                    des: 'Description',
                    color: 'Green',
                    name: 'car4'
                },
                {
                    des: 'Description',
                    color: 'Green',
                    name: 'car5'

                },
                {
                    des: 'Description',
                    color: 'Green',
                    name: 'car6'
                }
            ]
        }
        console.log(this.state);
    }
    updateDescription(name) {
        console.log(name);
        const index = this.state.description.findIndex((selectedDes) => {return name === selectedDes.name});
        console.log(index);
        if (true) {
            <div>
                <div class="modal" tabindex="-1" role="dialog">
                    <div class="modal-dialog" role="document">
                        <div class="modal-content">
                            <div class="modal-header">
                                <h5 class="modal-title">Modal title</h5>
                                <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">&times;</span>
                                </button>
                            </div>
                            <div class="modal-body">
                                <p>Modal body text goes here.</p>
                            </div>
                            <div class="modal-footer">
                                <button type="button" class="btn btn-primary">Save changes</button>
                                <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        }
    }
    render() {
        return (
            <div>
                <div className="container">
                    <div className="card m-2 h-100">
                        <div className="card-body">
                            <div className="row">
                                <div className="col">
                                    {this.state.description.map(data => (
                                        <div className="row">
                                            <div className="col-2 m-2"><button className="btn btn-outline-secondary">Add</button></div>
                                            <div className="col-6 m-2">{data.name} : {data.des}</div>
                                            <div className="col-1 m-2"><button onClick={() => this.updateDescription(data.name)} className="btn btn-outline-secondary">Update</button></div>
                                            <div className="col-1 m-2"><button className="btn btn-outline-secondary">Delete</button></div>
                                        </div>
                                    ))}
                                    {/* {this.state.description.des} , {this.state.description.color} */}
                                </div>
                            </div>

                        </div>
                    </div>
                </div>

            </div>
        )

    }
}

export default List;
