import React from 'react'
import { Modal } from 'react-bootstrap'

const style = {
    marginRight: 10
};
class List extends React.Component {
    constructor() {
        super()
        this.state = {
            description: [
                {
                    des: 'Description',
                    color: 'Green',
                    name: 'car1'
                },
                {
                    des: 'Description',
                    color: 'Green',
                    name: 'car2'
                },
                {
                    des: 'Description',
                    color: 'Green',
                    name: 'car3'
                },
                {
                    des: 'Description',
                    color: 'Green',
                    name: 'car4'
                },
                {
                    des: 'Description',
                    color: 'Green',
                    name: 'car5'

                },
                {
                    des: 'Description',
                    color: 'Green',
                    name: 'car6'
                }
            ],
            show : false
        }
        console.log(this.state);
    }
    updateDescription(name) {
        this.setState({
            show:false
        })
        console.log(name);
        const index = this.state.description.findIndex((selectedDes) => { return name === selectedDes.name });
        console.log(index);
        // if (true) {
        //     return ()
        // }
    }
    deleteDes(name) {
        const desArray = this.state.description;
        const index = this.state.description.findIndex((selectedDes) => { return name === selectedDes.name });
        const updatedDes = delete desArray[index];
        return updatedDes;
    }
    modalOpen(){
        this.setState({
            show:true
        })
    }
    render() {
        return (
            <div>
                <div className="container">
                    <div className="card m-2 h-100">
                        <div className="card-body">
                            <div className="row">
                                <div className="col">
                                    {this.state.description.map(data => (
                                        <div className="row">
                                            <div className="col-2 m-2"><button className="btn btn-outline-secondary">Add</button></div>
                                            <div className="col-6 m-2">{data.name} : {data.des}</div>
                                            <div className="col-1 m-2"><button onClick={() => this.modalOpen()} className="btn btn-outline-secondary">Update</button>
                                                <Modal show = {this.state.show}>
                                                    <Modal.Header>
                                                        Update Description
                                                    </Modal.Header>
                                                    <Modal.Body>
                                                        <label>Name :</label>
                                                        <input type="text">{data.name}</input>
                                                        <label>Description :</label>
                                                        <input type="text">{data.des}</input>
                                                        <label>Colour :</label>
                                                        <input type="text">{data.color}</input>
                                                    </Modal.Body>
                                                    <Modal.Footer>
                                                        <button onClick={() => this.updateDescription(data.name)}></button>
                                                    </Modal.Footer>
                                                </Modal>
                                            </div>
                                            <div className="col-1 m-2"><button onClick={() => this.deleteDes(data.name)} className="btn btn-outline-secondary">Delete</button></div>
                                        </div>
                                    ))}
                                    {/* {this.state.description.des} , {this.state.description.color} */}
                                </div>
                            </div>

                        </div>
                    </div>
                </div>

            </div>
        )

    }
}

export default List;
