                                                <Modal.Footer>
                                                    <div className="d-flex">
                                                        <button style={style} className="btn btn-outline-secondary">Close</button>
                                                        <button onClick={addToCart()} className="btn btn-outline-secondary">Add</button>
                                                    </div>
                                                </Modal.Footer>
                                            </Modal>
                                        </div>
                                    </div>
                                    <div className="col-1 m-2">
                                        <button onClick={deleteDes()} className="btn btn-outline-secondary">Delete</button>
                                    </div>
                                </div>
                                {/* {this.state.description.des} , {this.state.description.color} */}
                            </div>
                        </div>

                    </div>
                </div>
            </div>

        </div>
    )
}

export default List;
